import telebotfrom telebot.types import InlineKeyboardMarkup, InlineKeyboardButtonimport osfrom flask import Flask, request# ---  ---#         API_TOKEN = os.environ.get('BOT_TOKEN')WEBHOOK_URL_BASE = os.environ.get('WEBHOOK_URL')bot = telebot.TeleBot(API_TOKEN)server = Flask(__name__)# ---   () ---#  : (, URL/Handle)TOOLS = [    (".   ", "https://fb-fakeid-report-shadowjoker.vercel.app/"),    (".   ", "https://fb-disable-account-recover-shadowjo.vercel.app/"),    (".  SMS Bomber", "https://shadow-joker-hard-sms-bombar.vercel.app/"),    (".    ", "https://bangladesh-fake-nid-maker-shadow-jo.vercel.app/"),    (".    ", "https://t.me/IP_INFO_SHADOW_BOT"),    (".    ", "https://t.me/wether_info_shadow_bot"),    (".    NID", "http://bangladeshi-number-to-nid-tool-cht.vercel.app"),    (".  IMEI ", "https://imei-to-device-info-shadow.vercel.app/"),    (".    ", "https://t.me/shadow_free_image_hosting_bot"),    (".  Free Fire ", "https://ff-id-info-cyber-team-hlep.vercel.app/"),    (".   ", "https://birthday-online-copy-shadow-joker.vercel.app/"),    (".   ", "https://number-to-live-location-cyber-team.vercel.app/"),    (".   ", "https://cyber-team-help-web-cloner-shadow.vercel.app/"),    (".    (Drive)", "https://drive.google.com/folderview?id=1tgkKt4lSpXD3GnMQRgUb4bbtlmpP9XOE"),    (".   AI ", "https://shadow-joker-all-ai.vercel.app/"),    (".   ", "https://shadow-deface-website.vercel.app/"),    (".  Root Wifi Hack", "https://shadow-root-phone-wifi-hack.vercel.app/"),    (".  CTH  ", "https://shadow-cth-tool-joker.vercel.app/"),]PAGE_SIZE = 6 #    # ---    ---def generate_keyboard(page=0):    """                      Back/Next      """    markup = InlineKeyboardMarkup()    start_index = page * PAGE_SIZE    end_index = start_index + PAGE_SIZE        #         current_tools = TOOLS[start_index:end_index]        #        for name, url in current_tools:        #    URL            markup.add(InlineKeyboardButton(name, url=url))            #        nav_buttons = []        # Back     if page > 0:        nav_buttons.append(InlineKeyboardButton(" Back", callback_data=f"page_{page-1}"))        # Next     if end_index < len(TOOLS):        nav_buttons.append(InlineKeyboardButton("Next ", callback_data=f"page_{page+1}"))            if nav_buttons:        markup.row(*nav_buttons) #                 return markup, page# --- /start  ---@bot.message_handler(commands=['start'])def send_welcome(message):    user_name = message.from_user.first_name if message.from_user.first_name else "Dear"        #   ( )    welcome_text = (        f" *Hello {user_name}*,\n\n"        f" *Bot READY*\\!       \n\n"        f"\n"        f" **CYBER TEAM HELP**\n"        f" _CREATE BY SHADOW JOKER_"    )        keyboard, page = generate_keyboard(0)        bot.send_message(        message.chat.id,         welcome_text,        parse_mode="MarkdownV2", #            reply_markup=keyboard    )# ---     () ---@bot.callback_query_handler(func=lambda call: call.data.startswith('page_'))def callback_query(call):    #         try:        new_page = int(call.data.split('_')[1])    except:        bot.answer_callback_query(call.id, "    ")        return        keyboard, current_page = generate_keyboard(new_page)        #          try:        bot.edit_message_reply_markup(            chat_id=call.message.chat.id,            message_id=call.message.message_id,            reply_markup=keyboard        )        bot.answer_callback_query(call.id) #        except Exception as e:        #       (:   )        print(f"Error editing message: {e}")        bot.answer_callback_query(call.id, "     ")# --- Flask   ---@server.route('/' + API_TOKEN, methods=['POST'])def get_message():    """Telegram     """    if request.headers.get('content-type') == 'application/json':        json_string = request.get_data().decode('utf-8')        update = telebot.types.Update.de_json(json_string)        bot.process_new_updates([update])        return '', 200    else:        return '', 403@server.route('/')def index():    """   ()"""    return "<h1>Telegram Bot is Running!</h1>", 200# ---   ---def set_webhook():    """      """    if API_TOKEN and WEBHOOK_URL_BASE:        #              bot.remove_webhook()         #            webhook_url = f"{WEBHOOK_URL_BASE}/{API_TOKEN}"        bot.set_webhook(url=webhook_url)        print(f"Webhook set to: {webhook_url}")    else:        print("Error: BOT_TOKEN or WEBHOOK_URL is not set in environment variables.")#   if __name__ == "__main__":    set_webhook()    # Flask   8000   ,  Render-      server.run(host="0.0.0.0", port=int(os.environ.get('PORT', 8000)))